import React, { useState } from 'react';

const EXPENSE_CATEGORIES = ['Food', 'Transport', 'Entertainment', 'Shopping', 'Bills', 'Other'];
const INCOME_CATEGORIES = ['Salary', 'Freelance', 'Investment', 'Gift', 'Other'];

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isSignUp, setIsSignUp] = useState(false);
  const [transactions, setTransactions] = useState([
    { id: 1, amount: -45, type: 'expense', category: 'Food', description: 'Groceries' },
    { id: 2, amount: 500, type: 'income', category: 'Salary', description: 'Paycheck' },
    { id: 3, amount: -25, type: 'expense', category: 'Transport', description: 'Gas' },
  ]);
  const [showAll, setShowAll] = useState(false);
  const [formData, setFormData] = useState({
    amount: '',
    type: 'expense',
    category: 'Food',
    description: ''
  });
  const [authData, setAuthData] = useState({
    email: '',
    password: ''
  });

  const balance = transactions.reduce((sum, t) => sum + t.amount, 0);
  const displayedTransactions = showAll ? transactions : transactions.slice(0, 5);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.amount) return;

    const newTransaction = {
      id: Date.now(),
      amount: formData.type === 'expense' ? -Math.abs(parseFloat(formData.amount)) : Math.abs(parseFloat(formData.amount)),
      type: formData.type,
      category: formData.category,
      description: formData.description
    };

    setTransactions([newTransaction, ...transactions]);
    setFormData({
      amount: '',
      type: 'expense',
      category: 'Food',
      description: ''
    });
  };

  const deleteTransaction = (id) => {
    setTransactions(transactions.filter(t => t.id !== id));
  };

  const handleAuth = (e) => {
    e.preventDefault();
    setIsLoggedIn(true);
  };

  const getBalanceClass = () => {
    if (balance > 0) return 'positive';
    if (balance < 0) return 'negative';
    return 'zero';
  };

  if (!isLoggedIn) {
    return (
      <div className="auth-form">
        <h1 className="auth-title">Tally</h1>
        <form onSubmit={handleAuth}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              value={authData.email}
              onChange={(e) => setAuthData({...authData, email: e.target.value})}
              required
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={authData.password}
              onChange={(e) => setAuthData({...authData, password: e.target.value})}
              required
            />
          </div>
          <button type="submit" className="submit-btn">
            {isSignUp ? 'Sign Up' : 'Login'}
          </button>
        </form>
        <div className="auth-toggle">
          <button onClick={() => setIsSignUp(!isSignUp)}>
            {isSignUp ? 'Already have an account? Login' : 'Need an account? Sign Up'}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="header">
        <h1 className="title">Tally</h1>
        <div className={`balance ${getBalanceClass()}`}>
          ${balance.toFixed(2)}
        </div>
        <button className="logout-btn" onClick={() => setIsLoggedIn(false)}>
          Logout
        </button>
      </div>

      <form className="transaction-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Amount</label>
          <input
            type="number"
            step="0.01"
            placeholder="0.00"
            value={formData.amount}
            onChange={(e) => setFormData({...formData, amount: e.target.value})}
            required
          />
        </div>
        <div className="form-group">
          <label>Type</label>
          <div className="radio-group">
            <label>
              <input
                type="radio"
                value="expense"
                checked={formData.type === 'expense'}
                onChange={(e) => setFormData({
                  ...formData,
                  type: e.target.value,
                  category: e.target.value === 'expense' ? 'Food' : 'Salary'
                })}
              />
              Expense
            </label>
            <label>
              <input
                type="radio"
                value="income"
                checked={formData.type === 'income'}
                onChange={(e) => setFormData({
                  ...formData,
                  type: e.target.value,
                  category: e.target.value === 'expense' ? 'Food' : 'Salary'
                })}
              />
              Income
            </label>
          </div>
        </div>
        <div className="form-group">
          <label>Category</label>
          <select
            value={formData.category}
            onChange={(e) => setFormData({...formData, category: e.target.value})}
          >
            {(formData.type === 'expense' ? EXPENSE_CATEGORIES : INCOME_CATEGORIES).map(cat => (
              <option key={cat} value={cat}>{cat}</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Description</label>
          <input
            type="text"
            placeholder="Optional description"
            value={formData.description}
            onChange={(e) => setFormData({...formData, description: e.target.value})}
          />
        </div>
        <button type="submit" className="add-btn">Add Transaction</button>
      </form>

      <div className="transaction-list">
        {displayedTransactions.map(transaction => (
          <div key={transaction.id} className="transaction-item">
            <div className="transaction-info">
              <div className={`transaction-dot ${transaction.type}`}></div>
              <div className={`transaction-amount ${transaction.type}`}>
                {transaction.type === 'expense' ? '-' : '+'}${Math.abs(transaction.amount).toFixed(2)}
              </div>
              <div className="transaction-category">{transaction.category}</div>
              <div className="transaction-description">"{transaction.description}"</div>
            </div>
            <button
              className="delete-btn"
              onClick={() => deleteTransaction(transaction.id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      {transactions.length > 5 && !showAll && (
        <button className="show-more-btn" onClick={() => setShowAll(true)}>
          Show More
        </button>
      )}
    </div>
  );
}

export default App;
